import { getAuthInstance as getGapiAuthInstance } from 'google-auth-library';

const CLIENT_ID = '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';
const API_KEY = 'YOUR_API_KEY';
const DISCOVERY_DOCS = ['https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest'];
const SCOPES = 'https://www.googleapis.com/auth/calendar.events';

export const getAuthInstance = () => {
  return new Promise((resolve, reject) => {
    getGapiAuthInstance()
      .then((auth) => {
        if (auth.isSignedIn.get()) {
          resolve(auth);
        } else {
          auth.signIn().then(() => {
            resolve(auth);
          });
        }
      })
      .catch((error) => {
        reject(error);
      });
  });
};

export const insertEvent = (event) => {
  return new Promise((resolve, reject) => {
    getAuthInstance()
      .then((auth) => {
        const request = window.gapi.client.calendar.events.insert({
          calendarId: 'primary',
          resource: event,
        });

        request.execute((response) => {
          resolve(response);
        });
      })
      .catch((error) => {
        reject(error);
      });
  });
};

export const getEventLink = (event) => {
  return `https://calendar.google.com/event?action=TEMPLATE&text=${encodeURIComponent(
    event.summary
  )}&dates=${encodeURIComponent(event.start.dateTime)}/${encodeURIComponent(event.end.dateTime)}`;
};
