import React, { useState } from 'react';
import { ScheduleMeeting } from 'react-schedule-meeting';
import { getAuthInstance, getEventLink, insertEvent } from './googleCalendar.JSX';

const availableTimeslots = [0, 1, 2, 3, 4, 5].map((id) => {
  return {
    id,
    startTime: new Date(new Date().setDate(new Date().getDate() + id)).setHours(9, 0, 0, 0),
    endTime: new Date(new Date().setDate(new Date().getDate() + id)).setHours(17, 0, 0, 0),
  };
});

const CalendarModal = () => {
  const [selectedTime, setSelectedTime] = useState(null);

  const handleScheduleMeeting = () => {
    const email = prompt('Enter your email ID:');
    if (email) {
      const eventName = prompt('Enter the event name:');
      if (eventName) {
        const event = {
          summary: eventName,
          start: {
            dateTime: selectedTime.startTime.toISOString(),
          },
          end: {
            dateTime: selectedTime.endTime.toISOString(),
          },
          attendees: [{ email }],
        };

        insertEvent(event)
          .then((response) => {
            console.log('Event created:', response);
            window.open(getEventLink(response));
          })
          .catch((error) => {
            console.log('Error creating event:', error);
          });
      }
    }
  };

  return (
    <div className="modal fade" id="exampleModal" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title text-center" id="exampleModalLabel">
              Hire Me
            </h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div className="modal-body">
            <ScheduleMeeting
              borderRadius={10}
              primaryColor="#3f5b85"
              eventDurationInMinutes={30}
              availableTimeslots={availableTimeslots}
              onStartTimeSelect={(time) => setSelectedTime(time)}
            />
            <button className="btn btn-primary mt-3" onClick={handleScheduleMeeting}>
              Schedule Meeting
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarModal;
