import React,{ useState,useEffect }  from 'react';

import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'

const Modal = () => {
    const [selectedSlot, setSelectedSlot] = useState(null);
  const [userName, setUserName] = useState('venkatesh');
  const [userEmail, setUserEmail] = useState('venaktesh@gmail.com');
  const [meetingOption, setMeetingOption] = useState('googlemeet');

  const handleSlotSelect = (info) => {
    setSelectedSlot(info.startStr);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Perform any necessary form submission or data handling here
    console.log('Name:', userName);
    console.log('Email:', userEmail);
    console.log('Meeting Option:', meetingOption);
    console.log('Selected Slot:', selectedSlot);
    // Close the modal or perform any additional actions as needed
  };




    return (

<div class="modal fade" id="hireModal" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="hireModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
         <h5 className="modal-title" id="hireModalLabel">Hire Me</h5>
        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>  
        </div>
      <div class="modal-body">

          (
          <FullCalendar
            plugins={[dayGridPlugin]}
            initialView="dayGridMonth"
            dateClick={handleSlotSelect}
          />
        )
      </div>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Understood</button>
      </div>
    </div>
  </div>



    )
}
export default Modal
